import logo from './logo.svg';
import './App.css';
import { useState } from "react";
import { useForm } from 'react-hook-form'




function App() {
  const { register, handleSubmit, formState: { errors } } = useForm()

  console.log(errors)





  return (
    <div className="App">
      <form className="forr" onSubmit={handleSubmit((data) => {
        console.log(data);
      })} >
        <div className="colo">
          <div>
            <span>First Name<input type="text" {...register("firstName", { required: "this sis required" })} /> <br />
            </span>
          </div>
          <div>
            <span>Last Name<input type="text" {...register("lastName", { required: true })} /><br />
            </span>
          </div>
          <div>
            <span> Age<input type="text" name="age" {...register("age", {
              required: true, minLength: {
                value: 2,
                message: "min length is 2"
              }
            })} /><br />
            </span>
          </div>
          <div>
            <span>Email<input type="text" {...register("email", { required: true })} /><br />
            </span>
          </div>
          <button className="but">SUBMIT</button>,
        </div>
      </form >
    </div>


  );
}

export default App;
